import React, { Component } from 'react'
import Count from './components/Count'

class App extends Component {
  constructor(){
    super()
    // Declare State
    this.state={
      value: 0
    }
  }
  // Define our methods
  increment = () => {
    this.setState({
      value: this.state.value + 1
    })
  }
  // decrement count(value)
  decrment = () => {
    this.setState({
      value: this.state.value - 1
    })
  }
  render() {
    // destruct area 
    const { value } = this.state;
    const { increment, decrment } = this;
    return (
      <div>
        {/* Our components reside here */}
        <Count/>
      </div>
    )
  }
}

export default App
